// src/RecipePage.js
import React from 'react';
import './RecipePage.css'; // Import the CSS file for styling

const RecipePage = () => {
    return (
        <div className="recipe-page"> {/* Scoped styling */}
            <div className="recipe-container">
                <header>
                    <h1>Poha</h1>
                </header>
                <section className="description">
                    <h2>Description</h2>
                    <p>Poha is a savory Indian breakfast made from flattened rice, spiced with turmeric, mustard seeds, and green chilies, then mixed with onions, peas, and crunchy peanuts. Topped with fresh coriander and a squeeze of lemon, it’s a light yet flavorful dish.</p>
                </section>
                <section className="ingredients">
                    <h2>Ingredients</h2>
                    <ul>
                        <li>1 cup of flour</li>
                        <li>2 tablespoons of sugar</li>
                        <li>1 teaspoon of salt</li>
                        {/* Add more ingredients as needed */}
                    </ul>
                </section>
                <section className="procedure">
                    <h2>Procedure</h2>
                    <ol>
                        <li>Step 1: Preheat the oven to 180°C (350°F).</li>
                        <li>Step 2: Mix all dry ingredients in a bowl.</li>
                        <li>Step 3: Add wet ingredients and mix until smooth.</li>
                        {/* Add more steps as needed */}
                    </ol>
                </section>
                <section className="recipe-images">
                    <h2>Images</h2>
                    <div className="image-gallery">
                        <img src="" alt="Freshly baked dish" /> {/* More descriptive alt */}
                        <img src="" alt="Ingredients used for the recipe" /> {/* More descriptive alt */}
                        {/* Add more images with empty src as needed */}
                    </div>
                </section>
            </div>
        </div>
    );
};

export default RecipePage;
