import React from 'react';

const Cards = ({ image, title, text, linkComponent }) => {
  return (
    <div
      className="card"
      style={{
        width: "18rem",
        height: "25rem", // Fixed height for the card
        overflow: "hidden" // Ensures content stays within the card bounds
      }}
    >
      <img
        src={image}
        className="card-img-top"
        alt={title}
        style={{
          width: "100%",
          height: "12rem", // Fixed height for the image
          objectFit: "cover" // Ensures the image scales and crops to fill the space
        }}
      />
      <div className="card-body">
        <h5 className="card-title">{title}</h5>
        <p className="card-text">{text}</p>
        {linkComponent}
      </div>
    </div>
  );
};

export default Cards;
