// src/components/RecipeInfo.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function RecipeInfo() {
  const { category, id } = useParams(); // Include category parameter
  const [recipe, setRecipe] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch recipe by category and ID from the backend
    fetch(`http://localhost:4000/api/recipes${category}/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setRecipe(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching recipe:', error);
        setLoading(false);
      });
  }, [category, id]);

  if (loading) {
    return <p>Loading recipe...</p>;
  }

  if (!recipe) {
    return <p>Recipe not found.</p>;
  }

  return (
    <div className="container mt-4 text-white">
      <h1>{recipe.name}</h1>
      <p><strong>Description:</strong> {recipe.description}</p>
      <h3>Ingredients</h3>
      <ul>
        {recipe.ingredients.map((ingredient, idx) => (
          <li key={idx}>{ingredient}</li>
        ))}
      </ul>
      <h3>Directions</h3>
      <ol>
        {recipe.directions.map((step, idx) => (
          <li key={idx}>{step}</li>
        ))}
      </ol>
    </div>
  );
}

export default RecipeInfo;
