import React from 'react';
import { Link } from 'react-router-dom';

import cholebhature from '../images/CholeBhature.jpg';
import Cards from './cards';

const NorthIndian = () => {
  const cardData = [
    { 
      image: cholebhature, 
      title: 'Chole Bhature', 
      text: 'Explore delicious North Indian recipes.', 
      // link: 'api/recipes/northindian/672b77417adbe0af1634b804'
      id: '671fcaf1cdc8496499e89450' 
    },
    // Add other recipes here
  ];

  return (
    <div className="container mt-4">
      <div className="row justify-content-center">
        {cardData.map((card, index) => (
          <div key={index} className="col-12 col-sm-6 col-md-4 mb-4">
            <div className="d-flex justify-content-center">
              <Cards 
                image={card.image} 
                title={card.title} 
                text={card.text} 
                linkComponent={<Link to={`/recipes/northindian/${recipeId}`} className="btn btn-primary">Explore</Link>}

              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default NorthIndian;
