import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const RecipeDetails = () => {
  const { id } = useParams(); // Get recipe ID from URL params
  const [recipe, setRecipe] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:4000/api/recipes/category/${id}`)
      .then((response) => {
        setRecipe(response.data);
        console.log(response);
      })
      .catch((error) => {
        console.error('Error fetching recipe details:', error);
      });
  }, [id]);

  if (!recipe) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container mt-5 p-4" style={{ maxWidth: "700px", border: "1px solid #ddd", borderRadius: "8px", boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)", backgroundColor: "#fff" }}>
      <h1 style={{ fontSize: "2.5rem", fontWeight: "bold", color: "#333", marginBottom: "1rem" }}>{recipe.name}</h1>
      
      <p style={{ fontSize: "1.1rem", color: "#555", lineHeight: "1.6" }}>{recipe.description}</p>
  
      <h3 style={{ fontSize: "1.5rem", color: "#333", marginTop: "2rem" }}>Ingredients</h3>
      <ul style={{ listStyleType: "disc", paddingLeft: "1.5rem", color: "#444" }}>
        {recipe.ingredients.map((ingredient, index) => (
          <li key={index} style={{ fontSize: "1rem", marginBottom: "0.5rem" }}>{ingredient}</li>
        ))}
      </ul>
  
      <h3 style={{ fontSize: "1.5rem", color: "#333", marginTop: "2rem" }}>Directions</h3>
      <p style={{ fontSize: "1.1rem", color: "#555", lineHeight: "1.6" }}>{recipe.directions}</p>
  
      <div style={{ display: "flex", justifyContent: "flex-start", marginTop: "2rem" }}>
        <button style={{ backgroundColor: "#007bff", color: "#fff", border: "none", padding: "0.6rem 1.2rem", borderRadius: "4px", cursor: "pointer", fontSize: "1rem", fontWeight: "500", marginRight: "1rem" }}>Edit</button>
        <button style={{ backgroundColor: "#28a745", color: "#fff", border: "none", padding: "0.6rem 1.2rem", borderRadius: "4px", cursor: "pointer", fontSize: "1rem", fontWeight: "500" }}>Update</button>
      </div>
    </div>
  );
  
};

export default RecipeDetails;
